import ScrollReveal from 'scrollreveal'
import ImageSlider from '../common/ImageSlider.vue'
import Swiper from 'swiper'
import 'swiper/swiper-bundle.min.css'

import SlickSlider from './SlickSlider.vue'
import Feedback from './TheFeedback.vue'
import AOS from 'aos'
import 'aos/dist/aos.css'
import { onMounted, nextTick } from 'vue'

export default {
  name: 'ThePage',
  components: { ImageSlider, SlickSlider, Feedback },

  data() {
    return {
      mobileMenu: false,
      currentLanguage: 'English',
      imageUrl: require('@/assets/logo.png'),
      imageUrl1: require('@/assets/Group 1261152778.png'),
      imageUrl2: require('@/assets/Frame 1.png'),
      imageUrl3: require('@/assets/Frame 2.png'),
